dict.string:
entity_file="$TM_BUNDLE_SUPPORT/entities.txt" entity_list () { ${TM_RUBY:-ruby} <"$entity_file" -pe ' gsub(/^(\d+)\t(.+)$/) { |_| puts "#{[$1.to_i].pack("U")} — #{$2}" }' }
IFS=$(echo -e '\n\r') res=$(CocoaDialog dropdown --title "Insert Entity" \ --text "Select the character to insert" \ --button1 Okay \ --button2 Cancel \ --pulldown \ --exit-onchange \ --items "Character…" $(< <(entity_list)) )
[[ ${res:0:1} != "2" ]] && echo -n "&$(head -n${res:2} "$entity_file"|tail -n1|cut -f2);"
