dict.string:
#!/usr/bin/env ruby
# single tags single = /^img|meta|link|input|base|area|col|frame|param$/i single_no_arguments = /^br|hr$/i
tag = STDIN.read if(tag == '') print("<${1:p}>$2</${1/\\s.*//}>") elsif(single.match(tag)) print("<#{tag} $1/>") elsif(single_no_arguments.match(tag)) print("<#{tag} />") else print("<#{tag}>$1</#{tag.strip[/^\S+/]}>") end
