#!/usr/bin/env ruby
require 'tempfile'
input = Tempfile.new('tmtidy') input.close()
if(ENV['TM_TIDY']) tidy = ENV['TM_TIDY'] else tidy = 'tidy' end
File.open(input.path, File::WRONLY) { |fp| ENV['TM_SELECTED_TEXT'].to_s.each_line() { |line| fp.print(line) } }
output = `\"#{tidy}\" -utf8 -biq -asxhtml \"#{input.path}\" 2>/dev/null`
inbody = false output.each_line() { |line| if(inbody) if(line =~ /^\s*<\/body>\s*$/) inbody = false break else print(line) end elsif(line =~ /^\s*<body[^>]*>\s*$/) inbody = true end }
